fs.writeFileSync(uglifyAfterPreparePath, uglifyFile);

var uglifyConfigFile = fs.readFileSync(path.join(__dirname, '../uglify-config.json'));
fs.writeFileSync(path.join(paths[0], 'uglify-config.json'), uglifyConfigFile);

